import React, { Component } from "react";


class TextInput extends Component {
  constructor(props) {
    super(props);

    this.state = {
      inputText: "",
      isLoading: false,
      error: null,
      image: null,
      audio: null,
    };
  }

  handleInputChange = (event) => {
    this.setState({ inputText: event.target.value });
  };

  handleSubmit = () => {
    const { inputText } = this.state;
      const { apiEndpoint } = this.props;

      this.setState({ isLoading: true });

      const command = `python pipeline.py "${inputText}"`;

      exec(command, (error, stdout, stderr) => {
        if (error) {
          console.error(error);
          this.setState({ isLoading: false, error });
          return;
        }

        const result = JSON.parse(stdout);

        console.log(result);
        this.setState({
          isLoading: false,
          image: result.image,
          audio: result.audio,
        });
      });

  };
  
  

  render() {
    const { inputText, isLoading, error, image, audio } = this.state;

    return (
      <div>
        <input type="text" value={inputText} onChange={this.handleInputChange} />
        <button onClick={this.handleSubmit}>Submit</button>
        {isLoading && <p>Loading...</p>}
        {error && <p>Error: {error.message}</p>}
        {image && <img src={`data:image/png;base64,${image}`} alt="Generated Image" />}
        {audio && <audio src={`data:audio/wav;base64,${audio}`} controls />}
      </div>
    );

  }
}

export default TextInput;
