import React, { Component } from 'react';

class TextInput extends Component {
  constructor(props) {
    super(props);

    this.state = {
      inputText: "",
      isLoading: false,
      error: null,
      image: null,
      audio: null,
      control:false
    };
  }

  handleInputChange = (event) => {
    this.setState({ inputText: event.target.value });
  };

  handleSubmit = async () => {
    const { inputText } = this.state;

    try {
      const response = await fetch('http://127.0.0.1:5000/generate_output', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ inputText }),
      });
  
      const result = await response.json();
       console.log(result)
     
        this.setState({
          isLoading: false,
          control: true,
          audio: result.audioUrl
        });
      
    } catch (error) {
      console.error(error);
      this.setState({ isLoading: false, error });
    }
  };
  

  render() {
    const { inputText, isLoading, error } = this.state;
    
    return (
      <div>
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <input type="text" value={inputText} onChange={this.handleInputChange} />
        <button onClick={this.handleSubmit}>Submit</button>
        {isLoading && <p>Loading...</p>}
        {error && <p>Error: {error.message}</p>}
       
          <img src={"/assets/cat.jpg"} alt="" style={{ width: '40%' }} />
          <audio src={"/assets/output.wav"}controls />
        </div>
      </div>
    );
  }
}

export default TextInput;
