import React, { Component } from 'react';

class TextInput extends Component {
  constructor(props) {
    super(props);

    this.state = {
      inputText: "",
      isLoading: false,
      error: null,
      image: null,
      audio: null,
      control:false
    };
  }

  handleInputChange = (event) => {
    this.setState({ inputText: event.target.value });
  };

  handleSubmit = () => {
    const { inputText } = this.state;
   

  

    fetch('http://127.0.0.1:5000/generate_output', {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ inputText }),
    })
    .then((response) => response.json())
    .then((result) => {
      console.log(result);
      if (result.success) { // si la propiedad 'success' es true
        this.setState({
          isLoading: false,
          control: true, // establecer control en true para mostrar las imágenes
        });
      } else {
        // si la propiedad 'success' es false, mostrar un mensaje de error
        this.setState({ isLoading: false, error: "Error generating output." });
      }
    })
    .catch((error) => {
      console.error(error);
      this.setState({ isLoading: false, error });
    });
  };
  

  render() {
    const { inputText, isLoading, error, control } = this.state;

    return (
      <div>
        <input type="text" value={inputText} onChange={this.handleInputChange} />
        <button onClick={this.handleSubmit}>Submit</button>
        {isLoading && <p>Loading...</p>}
        {error && <p>Error: {error.message}</p>}
        {control ? (
          <>
            <img src={"/assets/cat.jpg"} alt="Generated Image" /> 
            <audio src={"/assets/output.wav"} controls />
          </>
        ) : (
           <p>Input text and click Submit to generate an image and audio.</p>
        )}
      </div>
    );
  }
}

export default TextInput;
